// cppcheck-suppress unusedFunction
char* LCore::encrypt(char *pass)
{
    char newpass[128];
    char val[10];
    memset(newpass, 0, sizeof(newpass));
    u_short i,len;
    u_char key = 0x43;

    len = strlen(pass)+1;

    sprintf(newpass, "%.2x%.2x%.2x", 0, len, key);

    pass[0] ^= (key | 'C');

    memset(val, 0, sizeof(val));
    sprintf(val, "%.2x", pass[0]);
    strcat(newpass, val);

    for(i = 1; i < len-1; i++)
    {
        pass[i] ^= pass[i-1] ^ key;
        memset(val, 0, sizeof(val));
        sprintf(val, "%.2x", pass[i]);
        strcat(newpass, val);
    }

    return strdup(newpass);
}

// cppcheck-suppress unusedFunction
int LCore::hex_decode(char *src, char *dst, int outsize)
{
    char *p, *q;

    const char *pe = src + strlen(src);
    const char *qe = dst + outsize;

    for (p = src, q = dst; p < pe && q < qe && isxdigit((int)*p); p += 2)
    {
        char ch = tolower(p[0]);
        char cl = tolower(p[1]);

        if ((ch >= '0') && (ch <= '9')) ch -= '0';
        else if ((ch >= 'a') && (ch <= 'f')) ch -= 'a' - 10;
        else return (-1);

        if ((cl >= '0') && (cl <= '9')) cl -= '0';
        else if ((cl >= 'a') && (cl <= 'f')) cl -= 'a' - 10;
        else return (-1);

        *q++ = (ch << 4) | cl;
    }
    return (q - dst);
}

// cppcheck-suppress unusedFunction
int LCore::decrypt(char *pass, int len)
{
    short i;
    char *p, key;

    if (len < 4)
        return (0);

    i = ntohs(*reinterpret_cast<short*>(pass));

    if (i != len - 2)
        return (0);

    key = pass[2];
    p = pass + 3;

    for (i -= 2; i > 0; i--)
        p[i] = p[i - 1] ^ p[i] ^ key;

    p[0] ^= (key | 'C');

    i = len - 3;
    memmove(pass, pass + 3, i);
    pass[i] = '\0';

    return (1);
}

