            out << "\tFontPath\t\"/usr/share/fonts/X11/75dpi/\"" << "\n";
            out << "EndSection" << "\n\n";

            out << "Section \"InputDevice\"" << "\n";
            out << "\tIdentifier\t\"Keyboard\"" << "\n";
            out << "\tDriver\t\"kbd\"" << "\n";
            out << "\tOption\t\"XkbRules\"\t\"xorg\"" << "\n";
