    if(!strSerialNumber.isEmpty())
    {
        strHostName = QString("ITL");
        strHostName.append(strSerialNumber.trimmed().remove("\""));
    }
    qDebug()<<"LCore:getHostnameForImpact::3:: strHostName: "<< strHostName;

